package com.sbi.scalablogs.futures

import scala.math._
import scala.concurrent._
import scala.concurrent.duration._
import scala.util.{ Success, Failure }
import scala.concurrent.forkjoin.ForkJoinPool
import java.math.MathContext

object Main extends App {
  val mc4 = new MathContext(4)
  val mc50 = new java.math.MathContext(50)
  val mc1000 = new java.math.MathContext(1000)
  val mc2000 = new java.math.MathContext(2000)
  implicit val mc4000 = new java.math.MathContext(4000)

  def piBBPdea(n: Int)(implicit mc: java.math.MathContext): Future[BigDecimal] = {

    def piBBPdeaTermI(i: Int)(mc: java.math.MathContext): BigDecimal = {
      BigDecimal(1.0)(mc) / BigDecimal(16)(mc).pow(i) * (
          BigDecimal(4.0)(mc) / (8 * i + 1) -
          BigDecimal(2.0)(mc) / (8 * i + 4) -
          BigDecimal(1.0)(mc) / (8 * i + 5) -
          BigDecimal(1.0)(mc) / (8 * i + 6)
        )
    }

    val result = Future((BigDecimal(0.0, mc) /: (0 to n).toList) { case (acc, i) => acc + piBBPdeaTermI(i)(mc) })
    println(s"Scheduled piBBPdea(${n})")
    result
  }

  val pi50 = BigDecimal("3.14159265358979323846264338327950288419716939937510", mc50)
  val pi1000 = BigDecimal("3.", mc1000)
  val pi2000 = BigDecimal("3.", mc2000)
  val pi4000 = BigDecimal("3.", mc4000)
  val ex = new ForkJoinPool(4)
  implicit val ec = ExecutionContext.fromExecutor( ex)

  val iterationCnt = 3200
  val compute = Future.sequence(Seq(
    piBBPdea(iterationCnt),
    piBBPdea(iterationCnt),
    piBBPdea(iterationCnt + 1)
    ))
  val startTime = System.currentTimeMillis
  compute.onComplete {
    case Success(t)  => {
      val stopTime = System.currentTimeMillis
      println("Done")
      println(s"Calculation time: ${1.0/1000 * (stopTime - startTime)}")
    }
    case Failure(ex) => println("Failed")
  }
  val result = Await.result(compute, 180 seconds)
  val stopTime2 = System.currentTimeMillis
  println(s"Calculation time: ${1.0/1000 * (stopTime2 - startTime)}")
  result foreach(res => println(s"Approx:   ${res}"))
  println(s"Correct : ${pi4000}")
  val delta = result.head(mc4000) - pi4000
  println(s"|Delta|: ${delta(mc4).abs}")
  ex.shutdown()
}